INSERT INTO `sdps_cluster_service_template_config` VALUES (917, 'content', '<!--\r\nLicensed to the Apache Software Foundation (ASF) under one or more\r\ncontributor license agreements.  See the NOTICE file distributed with\r\nthis work for additional information regarding copyright ownership.\r\nThe ASF licenses this file to You under the Apache License, Version 2.0\r\n(the \"License\"); you may not use this file except in compliance with\r\nthe License.  You may obtain a copy of the License at\r\n\r\nhttp://www.apache.org/licenses/LICENSE-2.0\r\n\r\nUnless required by applicable law or agreed to in writing, software\r\ndistributed under the License is distributed on an \"AS IS\" BASIS,\r\nWITHOUT WARRANTIES OR CONDITIONS OF ANY KIND, either express or implied.\r\nSee the License for the specific language governing permissions and\r\nlimitations under the License.\r\n-->\r\n<Configuration>\r\n  <Appenders>\r\n\r\n    <Console name=\"STDOUT\" target=\"SYSTEM_OUT\">\r\n      <PatternLayout>\r\n        <Pattern>\r\n          %d{ISO8601} [%t] %-5p [%X{collection} %X{shard} %X{replica} %X{core}] %C (%F:%L) - %m%n\r\n        </Pattern>\r\n      </PatternLayout>\r\n    </Console>\r\n\r\n    <RollingFile\r\n        name=\"RollingFile\"\r\n        fileName=\"{{infra_solr_log_dir}}/solr.log\"\r\n        filePattern=\"{{infra_solr_log_dir}}/solr.log.%i\" >\r\n      <PatternLayout>\r\n        <Pattern>\r\n          %d{ISO8601} [%t] %-5p [%X{collection} %X{shard} %X{replica} %X{core}] %C (%F:%L) - %m%n\r\n        </Pattern>\r\n      </PatternLayout>\r\n      <Policies>\r\n        <OnStartupTriggeringPolicy />\r\n        <SizeBasedTriggeringPolicy size=\"{{infra_log_maxfilesize}} MB\"/>\r\n      </Policies>\r\n      <DefaultRolloverStrategy max=\"{{infra_log_maxbackupindex}}\"/>\r\n    </RollingFile>\r\n\r\n    <RollingFile\r\n        name=\"SlowFile\"\r\n        fileName=\"{{infra_solr_log_dir}}/solr_slow_requests.log\"\r\n        filePattern=\"{{infra_solr_log_dir}}/solr_slow_requests.log.%i\" >\r\n      <PatternLayout>\r\n        <Pattern>\r\n          %d{ISO8601} [%t] %-5p [%X{collection} %X{shard} %X{replica} %X{core}] %C (%F:%L) - %m%n\r\n        </Pattern>\r\n      </PatternLayout>\r\n      <Policies>\r\n        <OnStartupTriggeringPolicy />\r\n        <SizeBasedTriggeringPolicy size=\"{{infra_log_maxfilesize}} MB\"/>\r\n      </Policies>\r\n      <DefaultRolloverStrategy max=\"{{infra_log_maxbackupindex}}\"/>\r\n    </RollingFile>\r\n\r\n  </Appenders>\r\n  <Loggers>\r\n    <Logger name=\"org.apache.hadoop\" level=\"warn\"/>\r\n    <Logger name=\"org.apache.solr.update.LoggingInfoStream\" level=\"off\"/>\r\n    <Logger name=\"org.apache.zookeeper\" level=\"warn\"/>\r\n    <Logger name=\"org.apache.solr.core.SolrCore.SlowRequest\" level=\"warn\" additivity=\"false\">\r\n      <AppenderRef ref=\"SlowFile\"/>\r\n    </Logger>\r\n\r\n    <Root level=\"warn\">\r\n      <AppenderRef ref=\"RollingFile\"/>\r\n      <!-- <AppenderRef ref=\"STDOUT\"/> -->\r\n    </Root>\r\n  </Loggers>\r\n</Configuration>', 'infra-solr-log4j', 31, NULL, NULL, NULL, 'varchar', 'content', 0, 0);
