INSERT INTO `sdps_cluster_service_template_config` VALUES (1100, 'content', '      #!/bin/bash\r\n\r\n      # Set DRUID specific environment variables here.\r\n\r\n      # The java implementation to use.\r\n      export JAVA_HOME={{java8_home}}\r\n      export PATH=$JAVA_HOME/bin:$PATH\r\n      export DRUID_PID_DIR={{druid_pid_dir}}\r\n      export DRUID_LOG_DIR={{druid_log_dir}}\r\n      export DRUID_CONF_DIR={{druid_conf_dir}}\r\n      export DRUID_LIB_DIR={{druid_home}}/lib\r\n      export HADOOP_CONF_DIR={{hadoop_conf_dir}}\",\"druid-env\",9999, \"varchar\"),\r\n(\"druid.coordinator.jvm.direct.memory\", \"druid.coordinator.jvm.direct.memory\",\"1048576\",\"druid-env\",9999, \"varchar\"),\r\n(\"druid.broker.jvm.heap.memory\", \"druid.broker.jvm.heap.memory\",\"2048\",\"druid-env\",9999, \"varchar\"),\r\n(\"druid.broker.jvm.direct.memory\", \"druid.broker.jvm.direct.memory\",\"1048576\",\"druid-env\",9999, \"varchar\"),\r\n(\"druid_pid_dir\", \"druid_pid_dir\",\"/var/run/druid\",\"druid-env\",9999, \"varchar\"),\r\n(\"druid.middlemanager.jvm.heap.memory\", \"druid.middlemanager.jvm.heap.memory\",\"256\",\"druid-env\",9999, \"varchar\"),\r\n(\"druid.historical.jvm.opts\", \"druid.historical.jvm.opts\",\"-Duser.timezone=UTC -Dfile.encoding=UTF-8 -Djava.util.logging.manager=org.apache.logging.log4j.jul.LogManager -Dorg.jboss.logging.provider=slf4j -Dnet.spy.log.LoggerImpl=net.spy.memcached.compat.log.SLF4JLogger -Dlog4j.shutdownCallbackRegistry=io.druid.common.config.Log4jShutdown -Dlog4j.shutdownHookEnabled=true -XX:+PrintGCDetails -XX:+PrintGCDateStamps -XX:+PrintGCTimeStamps -XX:+PrintGCApplicationStoppedTime -XX:+PrintGCApplicationConcurrentTime -Xloggc:/var/logs/druid/historical.gc.log -XX:+UseGCLogFileRotation -XX:NumberOfGCLogFiles=50 -XX:GCLogFileSize=10m -XX:+HeapDumpOnOutOfMemoryError -XX:HeapDumpPath=/var/logs/druid/historical.hprof -Djava.security.auth.login.config={druid_jaas_file}', 'druid-env', 24, NULL, NULL, NULL, 'varchar', 'content', 0, 0);
