RFA=org.apache.log4j.RollingFileAppender\r\nlog4j.appender.RFA.File=${hbase.log.dir}/${hbase.log.file}\r\n\r\nlog4j.appender.RFA.MaxFileSize=${hbase.log.maxfilesize}\r\nlog4j.appender.RFA.MaxBackupIndex=${hbase.log.maxbackupindex}\r\n\r\nlog4j.appender.RFA.layout=org.apache.log4j.PatternLayout\r\nlog4j.appender.RFA.layout.ConversionPattern=%d{ISO8601} %-5p [%t] %c{2}: %m%n\r\n\r\n#\r\n# Security audit appender\r\n#\r\nhbase.security.log.file=SecurityAuth.audit\r\nhbase.security.log.maxfilesize={{hbase_security_log_maxfilesize}}MB\r\nhbase.security.log.maxbackupindex={{hbase_security_log_maxbackupindex}}\r\nlog4j.appender.RFAS=org.apache.log4j.RollingFileAppender\r\nlog4j.appender.RFAS.File=${hbase.log.dir}/${hbase.security.log.file}\r\nlog4j.appender.RFAS.MaxFileSize=${hbase.security.log.maxfilesize}\r\nlog4j.appender.RFAS.MaxBackupIndex=${hbase.security.log.maxbackupindex}\r\nlog4j.appender.RFAS.layout=org.apache.log4j.PatternLayout\r\nlog4j.appender.RFAS.layout.ConversionPattern=%d{ISO8601} %p %c: %m%n\r\nlog4j.category.SecurityLogger=${hbase.security.logger}\r\nlog4j.additivity.SecurityLogger=false\r\n#log4j.logger.SecurityLogger.org.apache.hadoop.hbase.security.access.AccessController=TRACE\r\n\r\n#\r\n# Null Appender\r\n#\r\nlog4j.appender.NullAppender=org.apache.log4j.varia.NullAppender\r\n\r\n#\r\n# console\r\n# Add \"console\" to rootlogger above if you want to use this\r\n#\r\nlog4j.appender.console=org.apache.log4j.ConsoleAppender\r\nlog4j.appender.console.target=System.err\r\nlog4j.appender.console.layout=org.apache.log4j.PatternLayout\r\nlog4j.appender.console.layout.ConversionPattern=%d{ISO8601} %-5p [%t] %c{2}: %m%n\r\n\r\n# Custom Logging levels\r\n\r\nlog4j.logger.org.apache.zookeeper=INFO\r\n#log4j.logger.org.apache.hadoop.fs.FSNamesystem=DEBUG\r\nlog4j.logger.org.apache.hadoop.hbase=INFO\r\n# Make these two classes INFO-level. Make them DEBUG to see more zk debug.\r\nlog4j.logger.org.apache.hadoop.hbase.zookeeper.ZKUtil=INFO\r\nlog4j.logger.org.apache.hadoop.hbase.zookeeper.ZooKeeperWatcher=INFO\r\n#log4j.logger.org.apache.hadoop.dfs=DEBUG\r\n# Set this class to log INFO only otherwise its OTT\r\n# Enable this to get detailed connection error/retry logging.\r\n# log4j.logger.org.apache.hadoop.hbase.client.HConnectionManager$HConnectionImplementation=TRACE\r\n\r\n\r\n# Uncomment this line to enable tracing on _every_ RPC call (this can be a lot of output)\r\n#log4j.logger.org.apache.hadoop.ipc.HBaseServer.trace=DEBUG\r\n\r\n# Uncomment the below if you want to remove logging of client region caching\'\r\n# and scan of .META. messages\r\n# log4j.logger.org.apache.hadoop.hbase.client.HConnectionManager$HConnectionImplementation=INFO\r\n# log4j.logger.org.apache.hadoop.hbase.client.MetaScanner=INFO', 'hbase-log4j', 6, NULL, NULL, NULL, 'varchar', 'content', 0, 0);
