=[%d{ISO8601}] %p %m (%c)%n\n\nlog4j.appender.requestAppender=org.apache.log4j.DailyRollingFileAppender\nlog4j.appender.requestAppender.DatePattern=\'.\'yyyy-MM-dd-HH\nlog4j.appender.requestAppender.File=${kafka.logs.dir}/kafka-request.log\nlog4j.appender.requestAppender.layout=org.apache.log4j.PatternLayout\nlog4j.appender.requestAppender.layout.ConversionPattern=[%d{ISO8601}] %p %m (%c)%n\n\nlog4j.appender.cleanerAppender=org.apache.log4j.DailyRollingFileAppender\nlog4j.appender.cleanerAppender.DatePattern=\'.\'yyyy-MM-dd-HH\nlog4j.appender.cleanerAppender.File=${kafka.logs.dir}/log-cleaner.log\nlog4j.appender.cleanerAppender.layout=org.apache.log4j.PatternLayout\nlog4j.appender.cleanerAppender.layout.ConversionPattern=[%d{ISO8601}] %p %m (%c)%n\n\nlog4j.appender.controllerAppender=org.apache.log4j.DailyRollingFileAppender\nlog4j.appender.controllerAppender.DatePattern=\'.\'yyyy-MM-dd-HH\nlog4j.appender.controllerAppender.File=${kafka.logs.dir}/controller.log\nlog4j.appender.controllerAppender.layout=org.apache.log4j.PatternLayout\nlog4j.appender.controllerAppender.layout.ConversionPattern=[%d{ISO8601}] %p %m (%c)%n\nlog4j.appender.controllerAppender.MaxFileSize = {{controller_log_maxfilesize}}MB\nlog4j.appender.controllerAppender.MaxBackupIndex = {{controller_log_maxbackupindex}}\n# Turn on all our debugging info\n#log4j.logger.kafka.producer.async.DefaultEventHandler=DEBUG, kafkaAppender\n#log4j.logger.kafka.client.ClientUtils=DEBUG, kafkaAppender\n#log4j.logger.kafka.perf=DEBUG, kafkaAppender\n#log4j.logger.kafka.perf.ProducerPerformance$ProducerThread=DEBUG, kafkaAppender\n#log4j.logger.org.I0Itec.zkclient.ZkClient=DEBUG\nlog4j.logger.kafka=INFO, kafkaAppender\nlog4j.logger.kafka.network.RequestChannel$=WARN, requestAppender\nlog4j.additivity.kafka.network.RequestChannel$=false\n\n#log4j.logger.kafka.network.Processor=TRACE, requestAppender\n#log4j.logger.kafka.server.KafkaApis=TRACE, requestAppender\n#log4j.additivity.kafka.server.KafkaApis=false\nlog4j.logger.kafka.request.logger=WARN, requestAppender\nlog4j.additivity.kafka.request.logger=false\n\nlog4j.logger.kafka.controller=TRACE, controllerAppender\nlog4j.additivity.kafka.controller=false\n\nlog4j.logger.kafka.log.LogCleaner=INFO, cleanerAppender\nlog4j.additivity.kafka.log.LogCleaner=false\n\nlog4j.logger.state.change.logger=TRACE, stateChangeAppender\nlog4j.additivity.state.change.logger=false', 'kafka-log4j', 16, '2022-01-18 11:29:17', '2022-01-18 11:29:17', NULL, 'varchar', 'content', NULL, 0);
INSERT INTO `sdps_cluster_service_template_config` VALUES (2521, 'content', '\n{% if kerberos_security_enabled %}\nKafkaClient {\ncom.sun.security.auth.module.Krb5LoginModule required\nuseTicketCache=true\nrenewTicket=true\nserviceName=\"{{kafka_bare_jaas_principal}}\";\n};\n{% endif %}', 'kafka_client_jaas_conf', 16, '2022-01-18 11:29:17', '2022-01-18 11:29:17', NULL, 'varchar', 'content', NULL, 0);
INSERT INTO `sdps_cluster_service_template_config` VALUES (2522, 'content', '\n        /**\n        * Example of SASL/PLAIN Configuration\n        *\n        * KafkaServer {\n        *   org.apache.kafka.common.security.plain.PlainLoginModule required\n        *   username=\"admin\"\n        *   password=\"admin-secret\"\n        *   user_admin=\"admin-secret\"\n        *   user_alice=\"alice-secret\";\n        *   };\n        *\n        * Example of SASL/SCRAM\n        *\n        * KafkaServer {\n        *   org.apache.kafka.common.security.scram.ScramLoginModule required\n        *   username=\"admin\"\n        *   password=\"admin-secret\"\n        *   };\n        *\n        * Example of Enabling multiple SASL mechanisms in a broker:\n        *\n        *   KafkaServer {\n        *\n        *    com.sun.security.auth.module.Krb5LoginModule required\n        *    useKeyTab=true\n        *    storeKey=true\n        *    keyTab=\"/etc/security/keytabs/kafka_server.keytab\"\n        *    principal=\"kafka/kafka1.hostname.com@EXAMPLE.COM\";\n        *\n        *    org.apache.kafka.common.security.plain.PlainLoginModule required\n        *    username=\"admin\"\n        *    password=\"admin-secret\"\n        *    user_admin=\"admin-secret\"\n        *    user_alice=\"alice-secret\";\n        *\n        *    org.apache.kafka.common.security.scram.ScramLoginModule required\n        *    username=\"scram-admin\"\n        *    password=\"scram-admin-secret\";\n        *    };\n        *\n        **/\n\n        {% if kerberos_security_enabled %}\n\n        KafkaServer {\n        com.sun.security.auth.module.Krb5LoginModule required\n        useKeyTab=true\n        keyTab=\"{{kafka_keytab_path}}\"\n        storeKey=true\n        useTicketCache=false\n        serviceName=\"{{kafka_bare_jaas_principal}}\"\n        principal=\"{{kafka_jaas_principal}}\";\n        };\n        KafkaClient {\n        com.sun.security.auth.module.Krb5LoginModule required\n        useTicketCache=true\n        renewTicket=true\n        serviceName=\"{{kafka_bare_jaas_principal}}\";\n        };\n        Client {\n        com.sun.security.auth.module.Krb5LoginModule required\n        useKeyTab=true\n        keyTab=\"{{kafka_keytab_path}}\"\n        storeKey=true\n        useTicketCache=false\n        serviceName=\"zookeeper\"\n        principal=\"{{kafka_jaas_principal}}\";\n        };\n        com.sun.security.jgss.krb5.initiate {\n        com.sun.security.auth.module.Krb5LoginModule required\n        renewTGT=false\n        doNotPrompt=true\n        useKeyTab=true\n        keyTab=\"{{kafka_keytab_path}}\"\n        storeKey=true\n        useTicketCache=false\n        serviceName=\"{{kafka_bare_jaas_principal}}\"\n        principal=\"{{kafka_jaas_principal}}\";\n        };\n\n        {% endif %}', 'kafka_jaas_conf', 16, '2022-01-18 11:29:17', '2022-01-18 11:29:17', NULL, 'varchar', 'content', NULL, 0);
